selector_to_html = {"a[href=\"#shikijs-engine-oniguruma\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">@shikijs/engine-oniguruma<a class=\"headerlink\" href=\"#shikijs-engine-oniguruma\" title=\"Link to this heading\">#</a></h1><p>Engine for Shiki using Oniguruma RegExp engine in WebAssembly.</p><p><a class=\"tippyAnchor reference external\" href=\"https://shiki.style/guide/regex-engines\">Documentation</a></p>", "a[href=\"#license\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">License<a class=\"headerlink\" href=\"#license\" title=\"Link to this heading\">#</a></h2><p>MIT</p>"}
skip_classes = ["headerlink", "sd-stretched-link"]

window.onload = function () {
    for (const [select, tip_html] of Object.entries(selector_to_html)) {
        const links = document.querySelectorAll(`article.bd-article ${select}`);
        for (const link of links) {
            if (skip_classes.some(c => link.classList.contains(c))) {
                continue;
            }

            tippy(link, {
                content: tip_html,
                allowHTML: true,
                arrow: true,
                placement: 'auto-start', maxWidth: 500, interactive: false,

            });
        };
    };
    console.log("tippy tips loaded!");
};
