selector_to_html = {"a[href=\"#credits\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">Credits<a class=\"headerlink\" href=\"#credits\" title=\"Link to this heading\">#</a></h1><p>These definitions were written by <a class=\"tippyAnchor reference external\" href=\"https://github.com/lukeggchapman\">lukeggchapman</a>, <a class=\"tippyAnchor reference external\" href=\"https://github.com/rokt33r\">Junyoung Choi</a>, <a class=\"tippyAnchor reference external\" href=\"https://github.com/ChristianMurphy\">Christian Murphy</a>, and <a class=\"tippyAnchor reference external\" href=\"https://github.com/remcohaszing\">Remco Haszing</a>.</p>", "a[href=\"#additional-details\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Additional Details<a class=\"headerlink\" href=\"#additional-details\" title=\"Link to this heading\">#</a></h2>", "a[href=\"#summary\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">Summary<a class=\"headerlink\" href=\"#summary\" title=\"Link to this heading\">#</a></h1><p>This package contains type definitions for hast (https://github.com/syntax-tree/hast).</p>", "a[href=\"#details\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">Details<a class=\"headerlink\" href=\"#details\" title=\"Link to this heading\">#</a></h1><p>Files were exported from https://github.com/DefinitelyTyped/DefinitelyTyped/tree/master/types/hast.</p>", "a[href=\"#installation\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">Installation<a class=\"headerlink\" href=\"#installation\" title=\"Link to this heading\">#</a></h1>"}
skip_classes = ["headerlink", "sd-stretched-link"]

window.onload = function () {
    for (const [select, tip_html] of Object.entries(selector_to_html)) {
        const links = document.querySelectorAll(`article.bd-article ${select}`);
        for (const link of links) {
            if (skip_classes.some(c => link.classList.contains(c))) {
                continue;
            }

            tippy(link, {
                content: tip_html,
                allowHTML: true,
                arrow: true,
                placement: 'auto-start', maxWidth: 500, interactive: false,

            });
        };
    };
    console.log("tippy tips loaded!");
};
