selector_to_html = {"a[href=\"#uc-micro\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">uc.micro<a class=\"headerlink\" href=\"#uc-micro\" title=\"Link to this heading\">#</a></h1><p><a class=\"tippyAnchor reference external\" href=\"https://github.com/markdown-it/uc.micro/actions/workflows/ci.yml\"><img alt=\"CI\" loading=\"lazy\" src=\"https://github.com/markdown-it/uc.micro/actions/workflows/ci.yml/badge.svg\"/></a>\n<a class=\"tippyAnchor reference external\" href=\"https://www.npmjs.org/package/uc.micro\"><img alt=\"NPM version\" loading=\"lazy\" src=\"https://img.shields.io/npm/v/uc.micro.svg?style=flat\"/></a></p>"}
skip_classes = ["headerlink", "sd-stretched-link"]

window.onload = function () {
    for (const [select, tip_html] of Object.entries(selector_to_html)) {
        const links = document.querySelectorAll(`article.bd-article ${select}`);
        for (const link of links) {
            if (skip_classes.some(c => link.classList.contains(c))) {
                continue;
            }

            tippy(link, {
                content: tip_html,
                allowHTML: true,
                arrow: true,
                placement: 'auto-start', maxWidth: 500, interactive: false,

            });
        };
    };
    console.log("tippy tips loaded!");
};
